const moment = require('moment');
const phoneFormatter = require('phone-formatter');

exports.buildCustomerOrderEmail = (params) => {
  return new Promise((resolve, reject) => {
    Promise.all(params.products.map(product => product.product_image))
      .then(productImageUrls => {
        let emailBody = customerEmailTemplate;
        
        emailBody = emailBody.replace(/#CUSTOMER_NAME/g, params.customer_name);
        emailBody = emailBody.replace(/#DELIVERY_DATE/g, moment(params.delivery_date).format('dddd, MMMM Do, YYYY'));
        emailBody = emailBody.replace(/#ORDER_NO/g, params.order_no);
        emailBody = emailBody.replace(/#SHIPPING_ADDRESS/g, params.shipping_address);
        emailBody = emailBody.replace(/#SUBTOTAL/g, params.subtotal_price);
        emailBody = emailBody.replace(/#TOTAL/g, params.total_price);
        emailBody = emailBody.replace(/#DELIVERY_FEE/g, params.delivery_fee);
        emailBody = emailBody.replace(/#DISCOUNT_FEE/g, params.discount_fee);

        if (params.gratuity_fee === 'FREE') {
          emailBody = emailBody.replace(/#GRATUITY_FEE_STYLE/g, 'display: none');
        } else {
          emailBody = emailBody.replace(/#GRATUITY_FEE/g, params.gratuity_fee);
        }
      
        let productsBody = '';
        const rowCount = Math.ceil(params.products.length / 2);
        
        for (let index = 0; index < rowCount; index++) {
          const product1 = params.products[index * 2];
      
          let productBody = productColumnTemplate;
          productBody = productBody.replace(/#PRODUCT_NAME/g, `${product1.product_name} x${product1.quantity}`);
          productBody = productBody.replace(/#PRODUCT_OPTION/g, '');
          productBody = productBody.replace(/#PRODUCT_IMAGE/g, productImageUrls[index * 2]);
          productsBody += '<tr><td class="col_2_products">';
          productsBody += productBody;
      
          if (index * 2 + 1 < params.products.length) {
            const product2 = params.products[index * 2 + 1];
            productBody = productColumnTemplate;
            productBody = productBody.replace(/#PRODUCT_NAME/g, `${product2.product_name} x${product2.quantity}`);
            productBody = productBody.replace(/#PRODUCT_OPTION/g, '');
            productBody = productBody.replace(/#PRODUCT_IMAGE/g, productImageUrls[index * 2 + 1]);
            productsBody += productBody;
          }
      
          productsBody += '</td></tr>'
        }
      
        emailBody = emailBody.replace(/#PRODUCTS/g, productsBody);
      
        resolve(emailBody);
      })
      .catch(err => {
        reject(err);
      });
  });
}

exports.buildContactEmail = (params) => {
  let emailBody = contactEmailTemplate;

  emailBody = emailBody.replace(/#REPLY_URL/g, `mailto:${params.email}`);
  emailBody = emailBody.replace(/#DATE/g, moment().format('dddd, MMMM Do, YYYY'));
  emailBody = emailBody.replace(/#NAME/g, params.name);
  emailBody = emailBody.replace(/#EMAIL/g, params.email);
  emailBody = emailBody.replace(/#MESSAGE/g, params.message);
  emailBody = emailBody.replace(/#ID/g, '');

  if (params.attachment_url) {
    emailBody = emailBody.replace(/#ATTACHMENT/g, `<a href='${params.attachment_url}'>Download</a>`);
  } else {
    emailBody = emailBody.replace(/#ATTACHMENT/g, 'No Attachment');
  }

  return emailBody;
}

exports.buildAdminOrderEmail = (params) => {
  return new Promise((resolve, reject) => {
    Promise.all(params.products.map(product => product.product_image))
      .then(productImageUrls => {
        let emailBody = adminEmailTemplate;
        
        emailBody = emailBody.replace(/#CUSTOMER_NAME/g, params.customer_name);
        emailBody = emailBody.replace(/#DELIVERY_DATE/g, moment(params.delivery_date).format('dddd, MMMM Do, YYYY'));
        emailBody = emailBody.replace(/#ORDER_NO/g, params.order_no);
        emailBody = emailBody.replace(/#SHIPPING_ADDRESS/g, params.shipping_address);
        emailBody = emailBody.replace(/#SUBTOTAL/g, params.subtotal_price);
        emailBody = emailBody.replace(/#TOTAL/g, params.total_price);
        emailBody = emailBody.replace(/#DELIVERY_FEE/g, params.delivery_fee);
        emailBody = emailBody.replace(/#DISCOUNT_FEE/g, params.discount_fee);
        emailBody = emailBody.replace(/#EMAIL/g, params.email);
        emailBody = emailBody.replace(/#PAYMENT_METHOD/g, 'Credit Card');
        emailBody = emailBody.replace(/#SPECIAL_NOTES/g, params.special_notes);

        if (params.phone) {
          emailBody = emailBody.replace(/#PHONE/g, params.phone.length === 10 ? phoneFormatter.format(params.phone, '(NNN) NNN-NNNN') : params.phone);
        } else {
          emailBody = emailBody.replace(/#PHONE/g, '--');
        }

        if (params.gratuity_fee === 'FREE') {
          emailBody = emailBody.replace(/#GRATUITY_FEE_STYLE/g, 'display: none');
        } else {
          emailBody = emailBody.replace(/#GRATUITY_FEE/g, params.gratuity_fee);
        }
      
        let productsBody = '';
        const rowCount = Math.ceil(params.products.length / 2);
        
        for (let index = 0; index < rowCount; index++) {
          const product1 = params.products[index * 2];
      
          let productBody = productColumnTemplate;
          productBody = productBody.replace(/#PRODUCT_NAME/g, `${product1.product_name} x${product1.quantity}`);
          productBody = productBody.replace(/#PRODUCT_OPTION/g, '');
          productBody = productBody.replace(/#PRODUCT_IMAGE/g, productImageUrls[index * 2]);
          productsBody += '<tr><td class="col_2_products">';
          productsBody += productBody;
      
          if (index * 2 + 1 < params.products.length) {
            const product2 = params.products[index * 2 + 1];
            productBody = productColumnTemplate;
            productBody = productBody.replace(/#PRODUCT_NAME/g, `${product2.product_name} x${product2.quantity}`);
            productBody = productBody.replace(/#PRODUCT_OPTION/g, '');
            productBody = productBody.replace(/#PRODUCT_IMAGE/g, productImageUrls[index * 2 + 1]);
            productsBody += productBody;
          }
      
          productsBody += '</td></tr>';
        }
      
        emailBody = emailBody.replace(/#PRODUCTS/g, productsBody);
      
        resolve(emailBody);
      })
      .catch(err => {
        reject(err);
      });
  });
}

